mapper.select:
select * from job where 1=1

    and id = #{queryInfo.query} or name like "%"#{queryInfo.query}"%"


    and city_id = #{queryInfo.cityId}


    and major_id = #{queryInfo.majorId}


    and issue_status = #{queryInfo.issueStatus}


    and issue_date like #{queryInfo.issueDate}"%"

limit #{begin}, #{pageSize}

select issue_status
from job
where id = #{id}

select count(*) from job where id = #{queryInfo.query} or name like "%"#{queryInfo.query}"%"

    and city_id = #{queryInfo.cityId}


    and major_id = #{queryInfo.majorId}


    and issue_status = #{queryInfo.issueStatus}


    and issue_date like #{queryInfo.issueDate}"%"
